const project = 'PipedriveApp';
const crowdinJipt = '//cdn.crowdin.com/jipt/jipt.js';

function isCrowdInLang(lang) {
	return (lang === 'aa' || lang === 'aa-ER');
}

function be(lang) {
	if (!isCrowdInLang(lang)) {
		return '';
	}

	return '<script type="text/javascript">' +
		'var _jipt = [];' +
		`_jipt.push(['project', '${project}']);` +
		'</script>' +
		`<script type="text/javascript" src="${crowdinJipt}"></script>`;
}

function fe(lang) {
	if (!isCrowdInLang(lang)) {
		return;
	}

	const s = document.createElement('script');

	window._jipt = [['project', project]];
	s.type = 'text/javascript';
	s.src = crowdinJipt;
	document.getElementsByTagName('head')[0].appendChild(s);
}

module.exports = {
	be,
	fe,
};
