import { Button } from '@pipedrive/convention-ui-react';
import React from 'react';
import styled from 'styled-components';
import { useTranslator } from '@pipedrive/react-utils';

const Wrapper = styled.div`
	display: flex;
	justify-content: flex-end;
	margin-right: 8px;
`;

const SaveButton = styled(Button)`
	margin-left: 8px;
`;

interface Props {
	isLoading: boolean;
	onCancel: () => void;
	onSave: () => void;
}

export const ActionButtons: React.FC<Props> = ({ isLoading, onCancel, onSave }) => {
	const translator = useTranslator();

	return (
		<Wrapper>
			<Button size="s" onClick={onCancel}>
				{translator.gettext('Discard')}
			</Button>
			<SaveButton color="green" loading={isLoading} onClick={onSave} size="s">
				{translator.gettext('Save')}
			</SaveButton>
		</Wrapper>
	);
};
