import React from 'react';
import { useTranslator } from '@pipedrive/react-utils';
import { ToolbarButton, ToolbarButtonGroup, ToolbarButtonProps, ToolbarButtonType } from '@pipedrive/pd-wysiwyg';

export const Toolbar: React.FC<{ activeButtons?: ToolbarButtonType[] }> = ({ children, activeButtons = [] }) => {
	const translator = useTranslator();

	const buttons: ToolbarButtonProps[] = [
		{ type: 'bold', tooltipText: translator.gettext('Bold'), icon: 'bold' },
		{ type: 'italic', tooltipText: translator.gettext('Italic') },
		{ type: 'underline', tooltipText: translator.gettext('Underline'), icon: 'underline' },
		{ type: 'link', tooltipText: translator.gettext('Link'), icon: 'link' },
		{ type: 'ul', tooltipText: translator.gettext('Unordered list'), icon: 'list-bulleted' },
		{ type: 'ol', tooltipText: translator.gettext('Ordered list'), icon: 'list-numbered' },
		{ type: 'remove', tooltipText: translator.gettext('Remove style'), icon: 'clearformat' },
	];

	return (
		<>
			<ToolbarButtonGroup>
				{buttons.map((props) => (
					<ToolbarButton {...props} size="s" key={props.type} active={activeButtons.includes(props.type)} />
				))}
			</ToolbarButtonGroup>
			{children}
		</>
	);
};
