import React from 'react';
import { createFragmentContainer, graphql } from '@pipedrive/relay';

import { CustomField } from '../CustomField';
import { formatGraphQLTime } from '../format/formatGraphQLTime';

import type { CustomFieldTime_data } from './__generated__/CustomFieldTime_data.graphql';

type Props = {
	readonly data: CustomFieldTime_data;
};

const CustomFieldTimeWithoutData: React.FC<Props> = ({ data }) => {
	return (
		<CustomField
			icon="timeline"
			type="time"
			value={formatGraphQLTime(data.time)}
			data={data}
			isDisabled={true}
			onUpdate={() => Promise.resolve(false)}
		/>
	);
};

export const CustomFieldTime = createFragmentContainer(CustomFieldTimeWithoutData, {
	data: graphql`
		fragment CustomFieldTime_data on CustomFieldTime {
			...CustomField_data
			time
		}
	`,
});
