import React from 'react';
import styled from 'styled-components';
import { colors, fonts } from '@pipedrive/convention-ui-css/dist/js/variables';

import { FormFieldOption } from '../../types/types';

const SetValue = styled.span`
	font-size: ${fonts.fontSizeS};
	border-radius: 3px;
	border: solid 1px ${colors.black12Opaque};
	background-color: ${colors.white};
	display: inline-block;
	padding: 1px 3px;
	margin-right: 4px;
`;

type Props = {
	readonly options: FormFieldOption[];
};

export const DisabledMultipleOptionsField: React.FC<Props> = ({ options }) => (
	<>
		{options.map((option) => (
			<SetValue key={option.label ?? undefined} data-testid="CustomFieldMultipleOptionsValue">
				{option.label}
			</SetValue>
		))}
	</>
);
